mapper.select:
		SELECT b.*
		FROM (SELECT /*+INDEX_DESC(q pk_qna_ck)*/ ROWNUM rn, q.*
     		  FROM qna_ck q
      		  where user_id = #{userId}
      		  AND qna_title like '%'|| #{keyword} ||'%'


      		  AND qna_state = #{searchCondition}


      		  ) b

		WHERE b.rn <= (#{page}) * 5

		AND   b.rn > (#{page} - 1) * 5

SELECT COUNT(*)
FROM qna_ck
where user_id = #{userId}
AND qna_title like '%'|| #{keyword} ||'%'


AND qna_state = #{searchCondition}

select type2
from ask_type_ck
where type1 = #{type1}
